	#printf("%.3f\t",$score{$id});
	printf("%.3f|",$score);
	printf("%.3f",$score{$id}{plus});
	printf("%.3f\t",$score{$id}{minus});
	#print "$score{$id}{plus}$score{$id}{minus}\t";
		foreach my $head (@headers) {
